import React,{ Suspense,useState } from "react";
import {getValidationCode,validateAccessCode} from './action/AccessCodeAction'

import "./App.css";

const App = () => {
  const [phoneNumber,setPhoneNumber] = useState('');
  const [accessCode,setAccessCode] = useState('');
  const getAccessCode = ()=>{
    if (!phoneNumber) return ;

    try {
      getValidationCode(phoneNumber)
    } 
    catch (err) {
      console.log(err)
    }
  }
  const handleSubmit = (e)=>{
    if (!(accessCode&phoneNumber)) {
      alert('Need input');
      return ;
    }

    try {
      validateAccessCode(phoneNumber)
    } 
    catch (err) {
      console.log(err)
    }
  }
  return (
    // <ScreenSyllabus />
    <div className="App">
      <Suspense fallback={<h1>Loading....</h1>}>
        <div>
            <form style={style.main} onSubmit={handleSubmit}>
              <label>Phone Number</label>
              <input type='tel' required onChange={(e)=>setPhoneNumber(e.target.value)}/>
              <input type='button' value='Access Code' onSubmit={getAccessCode}/>
              <label>Access Code</label>
              <input type='text' required onChange={(e)=>setAccessCode(e.target.value)}/>
              <input type='submit' value='Validation'/>

            </form>
        </div>
        
      </Suspense>
    </div>
  );
}

export default App;
//

const style={
  main:{
    display:'flex',
    flexDirection:'column',
    justifyContent:'center',
    marginTop:'15vh'
    
  }
}
