import ServerDomain from "../serverdomain";
import axios from 'axios';

const getValidationCode = async (phoneNumber) => {
    
    
    const inputData = JSON.stringify({
      phoneNumber: phoneNumber
    })
    console.log()
    const accessCode = await axios.post(`${ServerDomain}/get`,inputData,{
                        headers: { 
                      "Content-Type":"application/json"
                    },
          })
          .then(resData=>{
                    console.log(`Get Success with ${resData}`)
                    return resData[0]
          })
          .catch(err=>{
                    const error= "Something went wrong. Check your input again"   
          });
            
}
const validateAccessCode =  (phoneNumber,accessCode) => {
    
    
    const inputData = JSON.stringify({
      phoneNumber: phoneNumber,
      accessCode:accessCode,
    })

    axios.post(`${ServerDomain}/validate`,inputData,{
                    headers: { 
                      "Content-Type":"application/json",
                    },
          })
          .then(resData=>{
                    console.log("Validation")
          })
          .catch(err=>{
                    const error= "Something went wrong. Check your input again"
                    throw new Error(error)
                    
                    
          });    
}

export {getValidationCode,validateAccessCode}
