import ServerDomain from "../serverdomain";
import axios from 'axios';

const getValidationCode =  (phoneNumber) => {
    
    
    const inputData = JSON.stringify({
      phoneNumber: phoneNumber
    })

    axios.post(`${ServerDomain}/get`,inputData,{
                    headers: { 
                      "Content-Type":"application/json"
                    },
          })
          .then(resData=>{
                    console.log("Get Success")
          })
          .catch(err=>{
                    const error= "Something went wrong. Check your input again"
                    throw new Error(error)
                    
                    
          });    
}
const validateAccessCode =  (phoneNumber,accessCode) => {
    
    
    const inputData = JSON.stringify({
      phoneNumber: phoneNumber,
      accessCode:accessCode,
    })

    axios.post(`${ServerDomain}/get`,inputData,{
                    headers: { 
                      "Content-Type":"application/json",
                    },
          })
          .then(resData=>{
                    console.log("Validation")
          })
          .catch(err=>{
                    const error= "Something went wrong. Check your input again"
                    throw new Error(error)
                    
                    
          });    
}

export {getValidationCode,validateAccessCode}
